mapper.select:
	SELECT * FROM EMPLOYEE
	WHERE NAME LIKE CONCAT('%',#{name},'%')
    AND EMPLOYEENUM != #{employeeNum}

SELECT * FROM ROOM
WHERE USER1=#{user1} AND USER2=#{user2}
   OR USER1=#{user2} AND USER2=#{user1}

SELECT EMPLOYEENUM, NAME, POSITION
FROM EMPLOYEE
WHERE EMPLOYEENUM=#{employeeNum}
